mapper.select:
select 

from teacher_balance_profits
where generate_date = #{generateDate,jdbcType=TIMESTAMP}
  and lessonTradeId = #{lessontradeid,jdbcType=VARCHAR}
  and teacherId = #{teacherid,jdbcType=VARCHAR}

mapper.delete:
delete from teacher_balance_profits
where generate_date = #{generateDate,jdbcType=TIMESTAMP}
  and lessonTradeId = #{lessontradeid,jdbcType=VARCHAR}
  and teacherId = #{teacherid,jdbcType=VARCHAR}

mapper.update:
update teacher_balance_profits


    balance_profit = #{balanceProfit,jdbcType=REAL},


    profit_rate = #{profitRate,jdbcType=REAL},


    actualPay = #{actualpay,jdbcType=REAL},


    balance_left = #{balanceLeft,jdbcType=REAL},


where generate_date = #{generateDate,jdbcType=TIMESTAMP}
  and lessonTradeId = #{lessontradeid,jdbcType=VARCHAR}
  and teacherId = #{teacherid,jdbcType=VARCHAR}

update teacher_balance_profits
set balance_profit = #{balanceProfit,jdbcType=REAL},
  profit_rate = #{profitRate,jdbcType=REAL},
  actualPay = #{actualpay,jdbcType=REAL},
  balance_left = #{balanceLeft,jdbcType=REAL}
where generate_date = #{generateDate,jdbcType=TIMESTAMP}
  and lessonTradeId = #{lessontradeid,jdbcType=VARCHAR}
  and teacherId = #{teacherid,jdbcType=VARCHAR}
