mapper.select:
select 

from teacher_balance_withdraw
where lessonTradeId = #{lessontradeid,jdbcType=VARCHAR}
  and teacherId = #{teacherid,jdbcType=VARCHAR}
  and withdraw_id = #{withdrawId,jdbcType=VARCHAR}

mapper.delete:
delete from teacher_balance_withdraw
where lessonTradeId = #{lessontradeid,jdbcType=VARCHAR}
  and teacherId = #{teacherid,jdbcType=VARCHAR}
  and withdraw_id = #{withdrawId,jdbcType=VARCHAR}

mapper.update:
update teacher_balance_withdraw


    apply_date = #{applyDate,jdbcType=TIMESTAMP},


    withdraw = #{withdraw,jdbcType=REAL},


where lessonTradeId = #{lessontradeid,jdbcType=VARCHAR}
  and teacherId = #{teacherid,jdbcType=VARCHAR}
  and withdraw_id = #{withdrawId,jdbcType=VARCHAR}

update teacher_balance_withdraw
set apply_date = #{applyDate,jdbcType=TIMESTAMP},
  withdraw = #{withdraw,jdbcType=REAL}
where lessonTradeId = #{lessontradeid,jdbcType=VARCHAR}
  and teacherId = #{teacherid,jdbcType=VARCHAR}
  and withdraw_id = #{withdrawId,jdbcType=VARCHAR}
